mapper.select:
SELECT
    *
FROM todos

    WHERE completed = #{completed}

ORDER BY todo_id
LIMIT #{limit}
OFFSET #{offset}

SELECT todos.todo_id AS todo_id, title, description, completed, tags.name as tag_name FROM todos LEFT OUTER JOIN todos_tags ON todos.todo_id = todos_tags.todo_id LEFT OUTER JOIN tags ON todos_tags.tag_name = tags.name WHERE todos.todo_id IN ( SELECT todos.todo_id FROM todos
WHERE completed = #{completed}
LIMIT #{limit} OFFSET #{offset} ) ORDER BY todos.todo_id
